require 'digest'
require 'gmp'
require 'openssl'

def dec_to_ascii(int)
  bin = int.to_s(2)
  padded_length = (bin.length / 8.0).ceil * 8
  bin.rjust(padded_length, '0').scan(/......../).map do |bin_str|
    bin_str.to_i(2).chr
  end.join
end

# Agreements
N = ''.to_i(16)
g = GMP::Z.new(2)
k = 3
$email = 'mail@herbert.id'
password = 'amelia'

# Secret things
c = Random.new.rand N
s = Random.new.rand N

# Server init
$salt = Random.new.bytes(16)
x = Digest::SHA256.hexdigest($salt + password).to_i(16)
server_v = g.powmod(x, N)

# C -> S, S -> C
$B = 1 # MALICIOUS
$A = g.powmod(c, N)
$u = Random.new.rand(2 ** 128)

# Compute S and K
client_S = GMP::Z.new($B).powmod(c + $u * x, N) # B^(c + ux) = g^(s(c + ux))
server_S = GMP::Z.new($A * GMP::Z.new(server_v).powmod($u, N)).powmod(s, N) # (Av^u)^s = g^(s(c + ux))
client_K = Digest::SHA256.digest dec_to_ascii client_S.to_i
server_K = Digest::SHA256.digest dec_to_ascii 1 # MALICIOUS

# Get hash
client_H = OpenSSL::HMAC.digest(OpenSSL::Digest.new('sha256'), client_K, $salt)
server_H = OpenSSL::HMAC.digest(OpenSSL::Digest.new('sha256'), server_K, $salt)
puts(client_H == server_H ? 'ok' : 'no')
